import React from 'react';
import './SubjectCards.css';

function SubjectCards({ subjects, onSubjectSelect, selectedSubject }) {
  return (
    <div className="subject-cards">
      {subjects.map(subject => (
        <div
          key={subject.id}
          className={`subject-card ${selectedSubject.id === subject.id ? 'active' : ''}`}
          onClick={() => onSubjectSelect(subject)}
          style={{ borderColor: subject.color }} // Border color based on subject
        >
          <h4>{subject.title}</h4>
          <p>{subject.color}</p>
        </div>
      ))}
    </div>
  );
}

export default SubjectCards;
