import React, { useState } from 'react';
import './TodoList.css'; // Ensure this file has the necessary styles

function ToDoList({ todos, onToggle, onAddTodo, subjectId }) {
  const [newTodo, setNewTodo] = useState('');

  const handleAddTodo = (e) => {
    e.preventDefault();
    if (newTodo) {
      onAddTodo(subjectId, newTodo); // Pass subjectId to addTodo
      setNewTodo('');
    }
  };

  return (
    <div className="todo-list">
      <form onSubmit={handleAddTodo}>
        <input
          type="text"
          value={newTodo}
          onChange={(e) => setNewTodo(e.target.value)}
          placeholder="Add a new task"
        />
        <button type="submit">Add</button>
      </form>
      <ul>
        {todos.map((todo, index) => (
          <li key={index} className={todo.completed ? 'completed' : ''}>
            <input
              type="checkbox"
              checked={todo.completed}
              onChange={() => onToggle(index)} // Trigger onToggle when checkbox is changed
            />
            {todo.text}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ToDoList;
