import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import "../styles/Form.css";

function Form({ onFormSubmit }) {
  const [subjectClassName, setSubjectClassName] = useState('');
  const [topicOfExam, setTopicOfExam] = useState('');
  const [learningGoals, setLearningGoals] = useState('');
  const [testDate, setTestDate] = useState('');
  const [availableStudySessions, setAvailableStudySessions] = useState('');
  const [workTimePerSession, setWorkTimePerSession] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    onFormSubmit({
      subjectClassName,
      topicOfExam,
      learningGoals,
      testDate,
      availableStudySessions,
      workTimePerSession,
    });
    navigate('/thankyou');
  };

  return (
    <form className="form" onSubmit={handleSubmit}>
      <div className="form-group">
        <label htmlFor="subjectClassName">Subject Class Name</label>
        <input
          type="text"
          id="subjectClassName"
          value={subjectClassName}
          onChange={(e) => setSubjectClassName(e.target.value)}
          placeholder="Enter the subject class name"
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="topicOfExam">Topic of Exam</label>
        <input
          type="text"
          id="topicOfExam"
          value={topicOfExam}
          onChange={(e) => setTopicOfExam(e.target.value)}
          placeholder="Enter the topic of the exam"
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="learningGoals">Learning Goals</label>
        <textarea
          id="learningGoals"
          value={learningGoals}
          onChange={(e) => setLearningGoals(e.target.value)}
          placeholder="Enter your learning goals"
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="testDate">Test Date</label>
        <input
          type="date"
          id="testDate"
          value={testDate}
          onChange={(e) => setTestDate(e.target.value)}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="availableStudySessions"># of Available Study Sessions Before Test</label>
        <input
          type="number"
          id="availableStudySessions"
          value={availableStudySessions}
          onChange={(e) => setAvailableStudySessions(e.target.value)}
          placeholder="Enter the number of available study sessions"
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="workTimePerSession">Work Time Per Session (in minutes)</label>
        <input
          type="number"
          id="workTimePerSession"
          value={workTimePerSession}
          onChange={(e) => setWorkTimePerSession(e.target.value)}
          placeholder="Enter the work time per session"
          required
        />
      </div>
      <button type="submit" className="submit-button">Submit</button>
    </form>
  );
}

export default Form;
