import React, { useState } from 'react';
import '../styles/ToDoList.css'; // Ensure this file has the necessary styles

function ToDoList({ todos, onToggle, onAddTodo, subjectId }) {
    const handleAddTodo = (e) => {
      e.preventDefault();
      const newTodo = e.target.todo.value.trim();
      if (newTodo) {
        onAddTodo(subjectId, newTodo);
        e.target.reset();
      }
    };

    return (
      <div className="todo-list">
        <form onSubmit={handleAddTodo}>
          <input type="text" name="todo" placeholder="Add new todo" required />
          <button type="submit">Add</button>
        </form>
        <ul>
          {todos.map((todo, index) => (
            <li key={index} className={todo.completed ? 'completed' : ''}>
              <label>
                <input
                  type="checkbox"
                  checked={todo.completed}
                  onChange={() => onToggle(index)} // Call the toggle function
                />
                {todo.text}
              </label>
            </li>
          ))}
        </ul>
      </div>
    );
  }

  export default ToDoList;
