import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { auth, googleProvider } from './firebaseConfig';
import { signInWithPopup, onAuthStateChanged, signOut } from 'firebase/auth';
import './styles/App.css';
import Dashboard from './components/Dashboard.js';
import Navbar from './components/Navbar.js';
import Form from './components/Form'; 
import SubmissionReviews from './components/SubmissionReviews'; 

function App() {
  const [user, setUser] = useState(null);
  const [titleClicked, setTitleClicked] = useState(false); // State to manage title animation

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  const handleSignIn = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
    } catch (error) {
      console.error('Error during sign-in:', error.message);
    }
  };

  const handleSignOut = () => {
    signOut(auth);
    setUser(null);
  };

  const handleTitleClick = () => {
    setTitleClicked(true); // Set the titleClicked state to true on click
  };

  return (
    <Router>
      <Navbar user={user} handleSignIn={handleSignIn} handleSignOut={handleSignOut} />
      <Routes>
        <Route
          path="/"
          element={
            user ? (
              <Dashboard user={user} />
            ) : (
              <div className="home-page">
                <h1 className={`welcome-title ${titleClicked ? 'fade-in' : ''}`} onClick={handleTitleClick}>
                  Welcome to Learn't
                </h1>
                <button className="sign-in-button" onClick={handleSignIn}>
                  Sign in with Google
                </button>
              </div>
            )
          }
        />
        <Route path="/dashboard" element={user ? <Dashboard user={user} /> : <Navigate to="/" />} />
        <Route path="/submission-review" element={<SubmissionReviews />} />
        <Route path="/form" element={<Form />} />
      </Routes>
    </Router>
  );
}

export default App;
