import { React, useEffect, useState } from 'react';
import { jsPDF } from 'jspdf'; // Import jsPDF
import "../styles/SubmissionReviews.css";

const SubmissionReviews = () => {
  const [studyGuide, setStudyGuide] = useState('');
  const [studyPlanner, setStudyPlanner] = useState('');
  const [subject, setSubject] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/data'); // Make sure to replace with the correct URL if necessary
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setStudyGuide(data.study_guide);
        setStudyPlanner(data.study_planner);
        setSubject(data.user_data.subject);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const saveToPDF = () => {
    const doc = new jsPDF();
    const pageWidth = doc.internal.pageSize.getWidth() - 20; // Leave some margin
    const pageHeight = doc.internal.pageSize.getHeight(); // Get the page height
    let verticalPosition = 20; // Starting vertical position for the text

    // Add title
    doc.setFontSize(18);
    doc.text(`Study Guide for ${subject}`, 10, verticalPosition);
    verticalPosition += 10; // Increase vertical position after the title

    // Wrap study guide text
    doc.setFontSize(12);
    const studyGuideLines = doc.splitTextToSize(studyGuide, pageWidth);

    // Add study guide text to PDF
    studyGuideLines.forEach(line => {
      if (verticalPosition + 10 > pageHeight) {
        doc.addPage(); // Add new page if vertical position exceeds page height
        verticalPosition = 10; // Reset vertical position
      }
      doc.text(line, 10, verticalPosition);
      verticalPosition += 10; // Increase vertical position after each line
    });

    // Add a new page for the study planner
    doc.addPage();
    verticalPosition = 10; // Reset vertical position for the new page
    doc.text(`Study Planner:`, 10, verticalPosition);
    verticalPosition += 10; // Increase vertical position after the title

    // Wrap study planner text
    const studyPlannerLines = doc.splitTextToSize(studyPlanner, pageWidth);

    // Add study planner text to PDF
    studyPlannerLines.forEach(line => {
      if (verticalPosition + 10 > pageHeight) {
        doc.addPage(); // Add new page if vertical position exceeds page height
        verticalPosition = 10; // Reset vertical position
      }
      doc.text(line, 10, verticalPosition);
      verticalPosition += 10; // Increase vertical position after each line
    });

    // Save the PDF
    doc.save(`${subject} Study Guide.pdf`);
  };


  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error fetching data: {error}</p>;
  }

  return (
    <div>
      {studyGuide ? (
        <>
        <div className='pdf-button-wrapper'>
          <button onClick={saveToPDF} className='study-guide-pdf-button'>
            Download {subject} Study Guide pdf
          </button>
        </div>
        <div className="submission-body">
          <div className="study-guide-wrapper">
            <h3>Study Guide for {subject}</h3>
            <pre>{studyGuide}</pre>
          </div>
          <div className="study-planner-wrapper">
            <h3>Study Planner</h3>
            <pre>{studyPlanner}</pre>
          </div>
        </div>

        </>
      ) : (
        <p>No submission data available. Please submit the form to generate study materials.</p>
      )}
    </div>
  );
};

export default SubmissionReviews;
