import React, { useState } from 'react';
import SubjectCards from './SubjectCards';
import TodoList from './ToDoList';
import ProgressWheel from './ProgressWheel.js';
import '../styles/Dashboard.css'; // Ensure this file is updated with styles

const subjects = [
  { id: 1, title: 'Mathematics', color: '#FF5733' },
  { id: 2, title: 'Science', color: '#33FF57' },
  { id: 3, title: 'History', color: '#3357FF' },
];

const initialTodoLists = {
  1: [],
  2: [],
  3: [],
};

function Dashboard({ user }) {
  const [activeSubject, setActiveSubject] = useState(subjects[0]);
  const [todoLists, setTodoLists] = useState(initialTodoLists);

  const addTodo = (subjectId, todo) => {
    setTodoLists((prevLists) => ({
      ...prevLists,
      [subjectId]: [...prevLists[subjectId], { text: todo, completed: false }],
    }));
  };

  const toggleTodo = (subjectId, index) => {
    setTodoLists((prevLists) => {
      const updatedTodos = [...prevLists[subjectId]];
      updatedTodos[index].completed = !updatedTodos[index].completed; // Toggle completion status
      return { ...prevLists, [subjectId]: updatedTodos };
    });
  };

  const getTotalProgress = () => {
    const totalCompleted = Object.values(todoLists).reduce((acc, list) => {
      return acc + list.filter(todo => todo.completed).length;
    }, 0);
    const totalItems = Object.values(todoLists).reduce((acc, list) => acc + list.length, 0);
    return totalItems ? (totalCompleted / totalItems) * 100 : 0;
  };

  const getSubjectProgress = (subjectId) => {
    const completed = todoLists[subjectId].filter(todo => todo.completed).length;
    const total = todoLists[subjectId].length;
    return total ? (completed / total) * 100 : 0;
  };

  return (
    <div className="dashboard">
      <div className="progress-section">
        <h2>Overall Progress</h2>
        <CircularProgress progress={getTotalProgress()} />
      </div>
      <div className="todo-section">
        <h2>{activeSubject.title} To-Do List</h2>
        <TodoList
          todos={todoLists[activeSubject.id]}
          onToggle={(index) => toggleTodo(activeSubject.id, index)}
          onAddTodo={addTodo} // Pass down the addTodo function
          subjectId={activeSubject.id} // Pass down the subjectId
        />
        <h3>{activeSubject.title} Progress</h3>
        <CircularProgress progress={getSubjectProgress(activeSubject.id)} />
      </div>
      <div className="subject-cards-section">
        <h2>Select a Subject</h2>
        <SubjectCards
          subjects={subjects}
          onSubjectSelect={setActiveSubject}
          selectedSubject={activeSubject}
        />
      </div>
    </div>
  );
}

export default Dashboard;
