import Particles from 'react-particles-js'; // If using a specific library, ensure to import it

function App() {
  // ... your existing code

  return (
    <Router>
      <Particles
        params={{
          particles: {
            number: {
              value: 100,
              density: {
                enable: true,
                value_area: 800,
              },
            },
            size: {
              value: 3,
            },
            move: {
              enable: true,
              speed: 1,
              direction: "none",
              random: false,
              straight: false,
              out_mode: "out",
            },
            // Add more particle configurations here if needed
          },
        }}
        style={{
          position: 'absolute',
          width: '100%',
          height: '100%',
          top: 0,
          left: 0,
          zIndex: 0, // Send to background
        }}
      />
      <Navbar user={user} handleSignIn={handleSignIn} handleSignOut={handleSignOut} />
      <Routes>
        <Route
          path="/"
          element={
            user ? (
              <Dashboard user={user} />
            ) : (
              <div className="home-page">
                <h1>Welcome to Learn't</h1>
                <button className="sign-in-button" onClick={handleSignIn}>
                  Sign in with Google
                </button>
              </div>
            )
          }
        />
        <Route path="/dashboard" element={user ? <Dashboard user={user} /> : <Navigate to="/" />} />
        <Route path="/submission-review" element={<SubmissionReviews />} />
        <Route path="/form" element={<Form />} />
        <Route path="/submission-review" element={<SubmissionReviews />} />
      </Routes>
    </Router>
  );
}
