import React from 'react';
import '../styles/ProgressWheel.css'; // Add styles as needed

const CircularProgress = ({ progress }) => {
  const radius = 50; // Radius of the circle
  const stroke = 10; // Width of the stroke
  const normalizedRadius = radius - stroke * 0.5; 
  const circumference = normalizedRadius * 2 * Math.PI;
  const strokeDashoffset = circumference - (progress / 100) * circumference;

  return (
    <svg height={radius * 2} width={radius * 2}>
      <circle
        stroke="#e6e6e6"
        fill="transparent"
        strokeWidth={stroke}
        r={normalizedRadius}
        cx={radius}
        cy={radius}
      />
      <circle
        stroke="#007aff" // Change this color based on subject or overall progress
        fill="transparent"
        strokeWidth={stroke}
        strokeDasharray={circumference + ' ' + circumference}
        style={{ strokeDashoffset }}
        r={normalizedRadius}
        cx={radius}
        cy={radius}
      />
      <text x="50%" y="50%" textAnchor="middle" stroke="#51c5cf" strokeWidth="1px" dy=".3em">
        {`${Math.round(progress)}%`}
      </text>
    </svg>
  );
};

export default CircularProgress;
